/* jqDebug.js | A tiny jquery debug function. | Author : @LukyVj | Date : Aug 2014*/
$(function(){function e(e){var t=$("[data-debug]");var n=t.attr("data-debug");t.each(function(){var t=$(this);if(e=="box-shadow"){t.css(e,"0 0 1em "+n)}else if(e=="border"){t.css(e,"1px solid "+n)}else{t.css(e,n)}})}e("box-shadow")})
